window.addEventListener("DOMContentLoaded",()=>{const observer=new IntersectionObserver(entries=>{entries.forEach(entry=>{const id=entry.target.getAttribute("id");if(entry.intersectionRatio>0)document.querySelectorAll("aside nav li a").forEach(section=>{section.classList.remove("active")});else{var element=document.querySelector(`nav li a[href="#${id}"]`);element&&element.parentElement.classList.remove("active")}})});document.querySelectorAll("h2[id]").forEach(section=>{observer.observe(section)}),document.querySelectorAll("h3[id]").forEach(section=>{observer.observe(section)})}),document.addEventListener("DOMContentLoaded",(function(){if(el_autohide=document.querySelector(".autohide"),el_autohide){var last_scroll_top=0;window.addEventListener("scroll",(function(){let scroll_top=window.scrollY;scroll_top<last_scroll_top?(el_autohide.classList.remove("scrolled-down"),el_autohide.classList.add("scrolled-up")):(el_autohide.classList.remove("scrolled-up"),el_autohide.classList.add("scrolled-down")),last_scroll_top=scroll_top}))}}));