window.addEventListener("DOMContentLoaded",()=>{const observer=new IntersectionObserver(entries=>{entries.forEach(entry=>{const id=entry.target.getAttribute("id");entry.intersectionRatio>0&&(document.querySelectorAll("aside nav li").forEach(section=>{section.classList.remove("active")}),document.querySelector(`nav li a[href="#${id}"]`).parentElement.classList.add("active"))})});document.querySelectorAll("h2[id]").forEach(section=>{observer.observe(section)}),document.querySelectorAll("h3[id]").forEach(section=>{observer.observe(section)})});