window.addEventListener("DOMContentLoaded",()=>{const observer=new IntersectionObserver(entries=>{entries.forEach(entry=>{const id=entry.target.getAttribute("id");if(entry.intersectionRatio>0)document.querySelectorAll("aside nav li a").forEach(section=>{section.classList.remove("active")});else{var element=document.querySelector(`nav li a[href="#${id}"]`);element&&element.parentElement.classList.remove("active")}})});document.querySelectorAll("h2[id]").forEach(section=>{observer.observe(section)}),document.querySelectorAll("h3[id]").forEach(section=>{observer.observe(section)})});const userLang=navigator.language||navigator.userLanguage;console.log(userLang),document.cookie=`nf_lang = ${userLang}`;